import React from 'react'
import ReactDOM from 'react-dom'
import styled from 'styled-components'

import firebase from 'firebase';

import ReactModal from 'react-modal'


// Initialize Firebase
var config = {
  apiKey: "AIzaSyBlJcSGcho_Jj27iPYrFH0pJcoaSugMYeM",
  authDomain: "fikrajob.firebaseapp.com",
  databaseURL: "https://fikrajob.firebaseio.com",
  projectId: "fikrajob",
  storageBucket: "fikrajob.appspot.com",
  messagingSenderId: "259365531926"
};

firebase.initializeApp(config);


let Button = styled.button`
  background-color: #466AB3;
  padding: 10px;
  border-radius: 8px;
  border: none;
  color: white;
  font-weight: bold;
  min-width: 100px;
`

let TextInput = styled.input`
  display: block;
  border: 2px solid #000;
  width: 100%;
  margin: 10px 0px;
  height: 40px;
  font-size: 1.4rem;
`


let Context = React.createContext()


let Navigation = styled.header`
  background-color: #fff;
  height: 120px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0px 10%;
`


class Header extends React.Component {

  constructor() {
    super()
    this.state = {

    }
  }

  render() {
    return (
      <Context.Consumer>
        {
          (ctx) => {
            return (
              <Navigation>
                <ReactModal isOpen={ctx.state.modalState}>
                  <h1>
                    FikraCamps
                  </h1>
                  <TextInput value={ctx.state.title} 
                  onChange={(event)=>{ctx.actions.onChangeTitle(event.target.value)}} 
                  placeholder="Title" type="text"/>

                  <TextInput 
                  onChange={(event)=>{ctx.actions.onChangeCompanyName(event.target.value)}} 
                  value={ctx.state.company_name}
                  placeholder="Company Name"  
                  type="text"/>

                  <Button onClick={()=>{

                    firebase.firestore().collection('jobs').add({
                      company_name: ctx.state.company_name,
                      title: ctx.state.title,
                      date: Date.now()
                    })

                    ctx.actions.toggle()
                  }}>Save</Button>

                </ReactModal>
                <img width="120px;" src={require('./assets/logo.png')} />
                <Button onClick={() => {
                  ctx.actions.toggle()
                }}>Post A Job</Button>
              </Navigation>
            )
          }
        }
      </Context.Consumer>
    )
  }
}


let Container = styled.main`
  background-color: red;
  min-height: 500px;
  padding: 10px 10%;

`
let Job = styled.div`
  height: 80px;
  border: 1px solid;
  background: #fff;
  border-radius: 4px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: sans-serif;
  font-size: 2rem;
  margin-top: 20px;
`

class JobsList extends React.Component {
  constructor() {
    super()
  }
  render() {
    return (
      <Context.Consumer>
        {(ctx) => {
          return <Container>
            {
              ctx.state.jobs.map((item, i) => {
                return <Job key={i}>{item.title}{i}</Job>
              })
            }
          </Container>
        }}
      </Context.Consumer>
    )
  }
}


class App extends React.Component {

  constructor() {
    super()
    this.state = {
      jobs: [{}],
      modalState: false,
      company_name: '',
      title: ''
    }

    firebase.firestore().collection('jobs').orderBy('date', 'asc').onSnapshot((snapshot)=>{
      let jobs = []

      snapshot.forEach((doc)=>{
        jobs.push(doc.data())
        this.setState({
          jobs: jobs
        })
      })
    })

  }

  render() {
    return (
      <Context.Provider value={{
        state: this.state,
        actions: {
          addJob: () => {

            firebase.firestore().collection('jobs').add({
              title: 'JS Developer',
              company_name: 'Twitter',
              date: Date.now()
            })

          },
          toggle: ()=>{
            this.setState({
              modalState: !this.state.modalState
            })
          },
          onChangeTitle: (value) =>{
            this.setState({
              title: value
            })
          },
          onChangeCompanyName: (value) =>{
            this.setState({
              company_name: value
            })
          }
        }
      }}>
        <Header />
        <JobsList />
      </Context.Provider>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('root'))
